mapper.select:
SELECT task_id, partition_id, status, last_processed_key
FROM compare_task_progress
WHERE task_id = #{taskId} AND partition_id = #{partitionId}

mapper.update:
UPDATE compare_task_progress
SET status = #{status},
    last_processed_key = #{lastProcessedKey}
WHERE task_id = #{taskId} AND partition_id = #{partitionId}
